import { useReducer } from 'react';
import BasketContext from './basket-context';

const defaultBasketState = {
  items: [],
  totalAmount: 0,
};

const basketReducer = (state, action) => {
  if (action.type === 'ADD') {
    const updatedTotal =
      state.totalAmount + action.item.price * action.item.amount;

    const existingBaksetItemIndex = state.items.findIndex(
      (item) => item === action.item.id
    );

    const existingBasketItem = state.items[existingBaksetItemIndex];
    let updatedItems;

    if (existingBasketItem) {
      const updatedItem = {
        ...existingBasketItem,
        amount: existingBasketItem.amount + action.item.amount,
      };
      updatedItems = [...state.items];
      updatedItems[existingBaksetItemIndex] = updatedItem;
    } else {
      updatedItems = state.items.concat(
        action.item
      ); /* concat updates and returns new state obj */
    }

    return {
      items: updatedItems,
      totalAmount: updatedTotal,
    };
  }
  return defaultBasketState;
};

const BasketProvider = (props) => {
  const [basketState, dispatchBasketAction] = useReducer(
    basketReducer,
    defaultBasketState
  );

  const addToBasketHandler = (item) => {
    dispatchBasketAction({ type: 'ADD', item: item });
  };

  const removeFromBasketHanlder = (id) => {
    dispatchBasketAction({ type: 'REMOVE', id: id });
  };

  const basketContext = {
    items: basketState.items,
    totalAmount: basketState.totalAmount,
    addItem: addToBasketHandler,
    removeItem: removeFromBasketHanlder,
  };

  return (
    <BasketContext.Provider value={basketContext}>
      {props.children}
    </BasketContext.Provider>
  );
};

export default BasketProvider;
